                <div className="absolute top-1/3 -right-6 sm:-right-8 w-1.5 h-1.5 sm:w-2 sm:h-2 lg:w-3 lg:h-3 bg-blue-300 rounded-full opacity-60 animate-float-3"></div>
                <div className="absolute top-1/4 -left-3 sm:-left-4 w-2.5 h-2.5 sm:w-3 sm:h-3 bg-cyan-400 rounded-full opacity-75 animate-float-4"></div>
                <div className="absolute top-2/3 -left-5 sm:-left-7 w-1.5 h-1.5 sm:w-2 sm:h-2 bg-indigo-400 rounded-full opacity-70 animate-float-6"></div>

                {/* Orbital circles with continuous rotation */}
                <div className="absolute inset-0 animate-orbit-slow">
                  <div className="absolute -top-4 sm:-top-6 left-1/2 w-1.5 h-1.5 sm:w-2 sm:h-2 bg-cyan-300 rounded-full opacity-50"></div>
                </div>
                <div className="absolute inset-0 animate-orbit-reverse">
                  <div className="absolute top-1/2 -right-5 sm:-right-7 w-1.5 h-1.5 sm:w-2 sm:h-2 bg-blue-300 rounded-full opacity-60"></div>
                </div>

                {/* Additional animated background effects */}
                <div className="absolute -inset-3 sm:-inset-4 bg-blue-600/10 rounded-full blur-2xl opacity-30 animate-pulse-slow"></div>
                <div className="absolute -inset-1.5 sm:-inset-2 bg-slate-800/30 rounded-full animate-float-bg"></div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}

