export interface SocialLink {
  platform: string;
  url: string;
  icon: 'github' | 'linkedin' | 'twitter' | 'instagram' | 'youtube' | 'dribbble' | 'behance';
}

export interface Award {
  title: string;
  year: number;
}

export interface AboutSection {
  _id: string;
  authorName: string;
  sectionTitle: string;
  bio: any[]; // Sanity block content
  portraitImage?: {
    asset: {
      _ref: string;
      _type: string;
      url?: string;
    };
    alt?: string;
  };
  socialLinks?: SocialLink[];
  awards?: Award[];
  skills?: string[];
}

// Keep backward compatibility
export interface AboutMe {
  _id: string;
  _type: 'about';
  bio: string;
  image?: {
    asset: {
      _ref: string;
      _type: 'reference';
      url?: string;
    };
  };
  skills?: string[];
}

export default AboutMe;

import { PortableTextBlock } from '@portabletext/types';

export interface SocialLink {
  platform: string;
  url: string;
  icon: 'github' | 'linkedin' | 'twitter' | 'instagram' | 'youtube' | 'dribbble' | 'behance';
}

export interface Award {
  title: string;
  year: number;
}

export interface AboutSection {
  _id: string;
  authorName: string;
  sectionTitle: string;
  bio: PortableTextBlock[]; // Sanity block content
  portraitImage?: {
    asset: {
      _ref: string;
      _type: string;
      url?: string;
    };
    alt?: string;
  };
  portraitVideo?: {
    asset: {
      _ref: string;
      _type: string;
      url?: string;
    };
  };
  resume?: {
    asset: {
      _ref: string;
      _type: string;
      url?: string;
    };
    title?: string;
  };
  socialLinks?: SocialLink[];
  awards?: Award[];
  skills?: string[];
}

// Keep backward compatibility
export interface AboutMe {
  _id: string;
  _type: 'about';
  bio: string;
  image?: {
    asset: {
      _ref: string;
      _type: 'reference';
      url?: string;
    };
  };
  skills?: string[];
}

export default AboutMe;

export interface HeroSection {
  _id: string;
  _type: "heroSection";
  headline: string;
  title: string;
  subtitle?: string;
  description?: string;
  ctaText?: string;
  ctaLink?: string;
  profileImage?: {
    asset: {
      _ref: string;
      _type: "reference";
      url?: string; // Available if resolved via GROQ or image URL builder
    };
  };
}

export interface HeroSection {
  _id: string;
  headline: string;
  title: string;
  subtitle?: string;
  description?: string;
  profileImage?: {
    asset: {
      _id: string;
      url: string;
    };
    alt?: string;
  };
  ctaText?: string;
  ctaLink?: string;
}

import { aboutMe } from './schema/aboutMe' import {experienceSection} from './schema/experienceSection' import { heroSection } from './schema/heroSection' import {contactSection} from './schema/contactSection' import {projectSection} from './schema/projectsSection' import {techStack} from './schema/techStack'
// @ts-ignore export const schemaTypes = [ contactSection, experienceSection, aboutMe, techStack, heroSection, projectSection,
]

import { defineField, defineType } from "sanity";

export const aboutMe = defineType({
  name: 'about',
  title: 'About Me',
  type: 'document',
  fields: [
    defineField({
      name: 'authorName',
      title: 'Author Name',
      type: 'string',
      validation: Rule => Rule.required()
    }),
    defineField({
      name: 'sectionTitle',
      title: 'Section Title',
      type: 'string',
      validation: Rule => Rule.required()
    }),
    defineField({
      name: 'bio',
      title: 'Bio/Description',
      type: 'array',
      of: [
        {
          type: 'block',
          styles: [
            {title: 'Normal', value: 'normal'},
            {title: 'H3', value: 'h3'},
            {title: 'Quote', value: 'blockquote'}
          ],
          marks: {
            decorators: [
              {title: 'Strong', value: 'strong'},
              {title: 'Emphasis', value: 'em'}
            ]
          }
        }
      ],
      validation: Rule => Rule.required()
    }),
    defineField({
      name: 'portraitImage',
      title: 'Portrait Image',
      type: 'image',
      options: {
        hotspot: true
      },
      fields: [
        {
          name: 'alt',
          type: 'string',
          title: 'Alternative Text',
          description: 'Important for SEO and accessibility.'
        }
      ]
    }),
    defineField({
      name: 'portraitVideo',
      title: 'Portrait Video (Optional - will override image if provided)',
      type: 'file',
      options: {
        accept: 'video/*'
      }
    }),
    defineField({
      name: 'socialLinks',
      title: 'Social Media Links',
      type: 'array',
      of: [
        {
          type: 'object',
          fields: [
            {
              name: 'platform',
              title: 'Platform Name',
              type: 'string',
              validation: Rule => Rule.required()
            },
            {
              name: 'url',
              title: 'URL',
              type: 'url',
              validation: Rule => Rule.required()
            },
            {
              name: 'icon',
              title: 'Platform Icon',
              type: 'string',
              options: {
                list: [
                  {title: 'GitHub', value: 'github'},
                  {title: 'LinkedIn', value: 'linkedin'},
                  {title: 'Twitter', value: 'twitter'},
                  {title: 'Instagram', value: 'instagram'},
                  {title: 'YouTube', value: 'youtube'},
                  {title: 'Dribbble', value: 'dribbble'},
                  {title: 'Behance', value: 'behance'}
                ]
              }
            }
          ]
        }
      ]
    }),
    defineField({
      name: 'awards',
      title: 'Awards & Recognition',
      type: 'array',
      of: [
        {
          type: 'object',
          fields: [
            {
              name: 'title',
              title: 'Award Title',
              type: 'string',
              validation: Rule => Rule.required()
            },
            {
              name: 'year',
              title: 'Year',
              type: 'number',
              validation: Rule => Rule.required().min(1900).max(new Date().getFullYear())
            }
          ]
        }
      ]
    }),
    defineField({
      name: 'skills',
      title: 'Skills',
      type: 'array',
      of: [{ type: 'string' }]
    })
  ],
  preview: {
    select: {
      title: 'authorName',
      subtitle: 'sectionTitle',
      media: 'portraitImage'
    }
  }
})

import { defineField, defineType } from "sanity";

export const aboutMe = defineType({
  name: 'about',
  title: 'About Me',
  type: 'document',
  fields: [
    defineField({
      name: 'authorName',
      title: 'Author Name',
      type: 'string',
      validation: Rule => Rule.required()
    }),
    defineField({
      name: 'sectionTitle',
      title: 'Section Title',
      type: 'string',
      validation: Rule => Rule.required()
    }),
    defineField({
      name: 'bio',
      title: 'Bio/Description',
      type: 'array',
      of: [
        {
          type: 'block',
          styles: [
            {title: 'Normal', value: 'normal'},
            {title: 'H3', value: 'h3'},
            {title: 'Quote', value: 'blockquote'}
          ],
          marks: {
            decorators: [
              {title: 'Strong', value: 'strong'},
              {title: 'Emphasis', value: 'em'}
            ]
          }
        }
      ],
      validation: Rule => Rule.required()
    }),
    defineField({
      name: 'portraitImage',
      title: 'Portrait Image',
      type: 'image',
      options: {
        hotspot: true
      },
      fields: [
        {
          name: 'alt',
          title: 'Alt Text',
          type: 'string',
          validation: Rule => Rule.required()
        }
      ]
    }),
    defineField({
      name: 'portraitVideo',
      title: 'Portrait Video (Optional)',
      type: 'file',
      options: {
        accept: 'video/*'
      }
    }),
    defineField({
      name: 'resume',
      title: 'Resume (PDF)',
      type: 'file',
      options: {
        accept: 'application/pdf'
      },
      fields: [
        {
          name: 'title',
          title: 'Resume Title',
          type: 'string',
          initialValue: 'Resume',
          validation: Rule => Rule.required()
        }
      ],
      description: 'Upload your resume in PDF format for download'
    }),
    defineField({
      name: 'skills',
      title: 'Technical Skills',
      type: 'array',
      of: [{ type: 'string' }],
      options: {
        layout: 'tags'
      }
    }),
    defineField({
      name: 'socialLinks',
      title: 'Social Media Links',
      type: 'array',
      of: [
        {
          type: 'object',
          fields: [
            {
              name: 'platform',
              title: 'Platform',
              type: 'string',
              validation: Rule => Rule.required()
            },
            {
              name: 'url',
              title: 'URL',
              type: 'url',
              validation: Rule => Rule.required().uri({ scheme: ['http', 'https'] })
            },
            {
              name: 'icon',
              title: 'Icon',
              type: 'string',
              options: {
                list: [
                  { title: 'GitHub', value: 'github' },
                  { title: 'LinkedIn', value: 'linkedin' },
                  { title: 'Twitter', value: 'twitter' },
                  { title: 'Instagram', value: 'instagram' },
                  { title: 'YouTube', value: 'youtube' },
                  { title: 'Dribbble', value: 'dribbble' },
                  { title: 'Behance', value: 'behance' }
                ]
              },
              validation: Rule => Rule.required()
            }
          ],
          preview: {
            select: {
              title: 'platform',
              subtitle: 'url'
            }
          }
        }
      ]
    }),
    defineField({
      name: 'awards',
      title: 'Awards & Recognition',
      type: 'array',
      of: [
        {
          type: 'object',
          fields: [
            {
              name: 'title',
              title: 'Award Title',
              type: 'string',
              validation: Rule => Rule.required()
            },
            {
              name: 'year',
              title: 'Year',
              type: 'number',
              validation: Rule => Rule.required().min(1900).max(new Date().getFullYear())
            }
          ],
          preview: {
            select: {
              title: 'title',
              subtitle: 'year'
            }
          }
        }
      ]
    })
  ],
  preview: {
    select: {
      title: 'authorName',
      subtitle: 'sectionTitle'
    }
  }
});

import { defineField } from "sanity";
import { defineType } from "sanity";

export const heroSection = defineType({
  name: 'heroSection',
  title: 'Hero Section',
  type: 'document',
  fields: [
    defineField({name: 'headline', type: 'string', validation: (rule) => rule.required()}),
    defineField({name: 'title', type: 'string', validation: (rule) => rule.required()}),
    defineField({name: 'subtitle', type: 'string'}),
    defineField({name: 'description', type: 'text'}),
    defineField({name: 'ctaText', type: 'string'}),
    defineField({name: 'ctaLink', type: 'url'}),
    defineField({name: 'profileImage', type: 'image'}),
  ],
})

import { defineField, defineType } from "sanity";

export const heroSection = defineType({
  name: 'hero',
  title: 'Hero Section',
  type: 'document',
  fields: [
    defineField({
      name: 'headline',
      title: 'Headline',
      type: 'string',
      validation: Rule => Rule.required()
    }),
    defineField({
      name: 'title',
      title: 'Main Title',
      type: 'string',
      validation: Rule => Rule.required()
    }),
    defineField({
      name: 'subtitle',
      title: 'Subtitle',
      type: 'string'
    }),
    defineField({
      name: 'description',
      title: 'Description',
      type: 'text'
    }),
    defineField({
      name: 'profileImage',
      title: 'Profile Image',
      type: 'image',
      options: {
        hotspot: true
      },
      fields: [
        {
          name: 'alt',
          type: 'string',
          title: 'Alternative Text',
          description: 'Important for SEO and accessibility.'
        }
      ]
    }),
    defineField({
      name: 'ctaText',
      title: 'CTA Button Text',
      type: 'string'
    }),
    defineField({
      name: 'ctaLink',
      title: 'CTA Button Link',
      type: 'string'
    })
  ],
  preview: {
    select: {
      title: 'title',
      subtitle: 'subtitle',
      media: 'profileImage'
    }
  }
})
